package condicionales;

public class Ejemplo_Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
	 int a=3;
	 
	 switch(a) 
	 {
	 case 0:
		 System.out.println("a es cero");
		 break;
	 case 1:
		 System.out.println("a es uno");
		 break;
	 case 2:
		 System.out.println("a es dos");
		 break;
	 case 3:
		 System.out.println("a es tres");
		 break;
	 case 4:
		 System.out.println("a es cuatro");
		 break;
	 case 5:
		 System.out.println("a es cinco");
		 break;
	default:
			 System.out.println("a no equivale a las condiciones anteriores");
	 }

	}

}
